package Control_statement;

public class controlstatemnt {


	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=10;
		
	//simpleif
		if(num%2==0) {//true
		System.out.println("even number");
		}

	
	// if-else statement
		if (num % 2 == 0) {
		    System.out.println("even number");
		} else {
		    System.out.println("odd number");
		}
		
	
	// nested if statement
		if (num % 2 == 0) {
		    System.out.println("even number");
		} else {
		    if (num % 2 != 0) {
		        System.out.println("odd number");
		        
       
		    }
		}
	}
}
		


	
	
	

	
	
		
