package Operators;

public class Assignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=10,num2=100;
		
//(+=)
		num1+=num2;//num1=num1+num2
		System.out.println("+=:"+num1);
//(-=)
		num1-=num2;//num1=num1-num2
		System.out.println("-=:"+num1);
//(*=)
		num1*=num2;//num1=num1*num2
		System.out.println("*=:"+num1);
//(/=)
		num1/=num2;//num1=num1/num2
		System.out.println("/=:"+num1);

//(%=)
		num1%=num2;//num1=num1%num2
		System.out.println("%:"+num1);


	}

}
