package Operators;

public class Ternary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//variable=exp1?exp2:exp3
		  int num1 = 4;
	        int num2 = 7;

	        //relational operator
	        int var1 = (num1 < num2) ? num1 - 1 : num2 + 1;//TRUE
	        System.out.println("Value of var1: " + var1);
	        
	        int n1 = 8;
	        int n2 = 5;
	        int var2 = (n1 < n2) ? n1 - 1 : n2 + 1;//FALSE
	        System.out.println("Value of var2: " + var2);
	        
	    }
	
				
	}


