package javaproject;

import java.util.Scanner;

public class inputusingscanner {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name="Ammu";
		Scanner sc=new Scanner(System.in);
		System.out.println("Enter your name:");
		String Name=sc.next();
		
		System.out.println("Enter your age:");
		int age=sc.nextInt();
		
		System.out.println("Enter your gender:");
		String gender=sc.next();
		
		System.out.println("Enter your Total_mark:");
		float totl=sc.nextFloat();
		
		System.out.println("Enter your course:");
		String course=sc.next();
		
		System.out.println("Enter your fees:");
		int fees=sc.nextInt();
		
		System.out.println("Enter your ph_no:");
		long ph_no=sc.nextLong();
		
		System.out.println("Details");
		System.out.println("Name="+name);
		System.out.println("Age="+age);
		System.out.println("Gender="+gender);
		System.out.println("Total_mark="+totl);
		System.out.println("Course="+course);
		System.out.println("Fees="+fees);
		System.out.println("Ph_no="+ph_no);
		
		




		
		
		

		
		


		



		
	}

}
